import React, { useState, useReducer } from 'react';

import Card from '../UI/Card/Card';
import classes from './Login.module.css';
import Button from '../UI/Button/Button';

//define your dispatFn outside of scope as it takes in no props
const emailReducer = (state, action) => {
  if(action.type === 'USER_INPUT'){
    return { value: action.val, isValid: action.val.includes('@') };
  }
  if(action.type === 'INPUT_BLUR'){
    return { value: state.value, isValid: state.value.includes('@') };
  }
  return { value:'', isValid: null };
}

const passwordReducer = (state, action) => {
 if( action.type === 'USER_INPUT_PW'){
   return { value: action.val, isValid: (action.val).trim().length > 6 };
 }
 if(action.type === 'INPUT_BLUR'){
   return { value: state.value, isValid: state.value.trim().length > 6};
 }
 return { value:'', isValid:false };
}

const Login = (props) => {

  const [emailState, dispatchEmailState] = useReducer(emailReducer, {value:'', isValid:false} );

  const [passwordState, dispatchPasswordState] = useReducer(passwordReducer, {value:'', isValid:false});

  const [formIsValid, setFormIsValid] = useState(emailState.isValid && passwordState.isValid);

  // useEffect(()=>{
  //   setFormIsValid(
  //     emailState.value.includes('@') && enteredPassword.trim().length > 6
  //   );
  // }, [ emailState, enteredPassword]);
  //This tells React to run only if any of the dependencies have changed.

  const emailChangeHandler = (event) => {
    dispatchEmailState({type:'USER_INPUT', val: event.target.value });

    setFormIsValid(
      emailState.value.includes('@') && passwordState.value.trim().length > 6
    );
  };

  const passwordChangeHandler = (event) => {
    dispatchPasswordState({type:'USER_INPUT_PW', val: event.target.value});

    setFormIsValid(
      emailState.isValid && passwordState.value.trim().length > 6
    );
  };

  const validateEmailHandler = () => {
    dispatchEmailState({type: 'INPUT_BLUR'});
  };

  const validatePasswordHandler = () => {
    dispatchPasswordState({type:'INPUT_BLUR'});
  };

  const submitHandler = (event) => {
    event.preventDefault();
    props.onLogin(emailState.value, passwordState.value);
  };

  return (
    <Card className={classes.login}>
      <form onSubmit={submitHandler}>
        <div
          className={`${classes.control} ${
            emailState.isValid === false ? classes.invalid : ''
          }`}
        >
          <label htmlFor="email">E-Mail</label>
          <input
            type="email"
            id="email"
            value={emailState.value}
            onChange={emailChangeHandler}
            onBlur={validateEmailHandler}
          />
        </div>
        <div
          className={`${classes.control} ${
            passwordState.isValid === false ? classes.invalid : ''
          }`}
        >
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            value={passwordState.value}
            onChange={passwordChangeHandler}
            onBlur={validatePasswordHandler}
          />
        </div>
        <div className={classes.actions}>
          <Button type="submit" className={classes.btn} disabled={!formIsValid}>
            Login
          </Button>
        </div>
      </form>
    </Card>
  );
};

export default Login;
